function mapStateToProps({
  authedUser,
  users,
  questions
}) {
  const user = users[authedUser];
  const answeredQuestions = Object.keys(user.answers);
  return {
    remaningQuestions: Object.keys(questions).filter(id => !answeredQuestions.includes(id)).sort((a, b) => questions[b].timestamp - questions[a].timestamp),
    answeredQuestions: answeredQuestions.sort((a, b) => questions[b].timestamp - questions[a].timestamp),
    user
  };
}

export default connect(mapStateToProps)(Dashboard);
