import { RECEIVE_QUESTIONS, ADD_RESPONSE } from '../actions/questions'

export default function questions (state = {}, action) {
  switch(action.type) {
    case RECEIVE_QUESTIONS : 
      return {
        ...state,
        ...action.questions,
      }
    case ADD_RESPONSE : 
     return {
       ...state,
       [action.qid] : {
         ...state[action.qid],
         [action.answer] : {
           ...state[action.qid][action.answer],
           votes: state[action.qid][action.answer].votes.concat([action.authedUser])
         }         
       }
     }
    default : 
      return state
  }
}
