import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Form } from 'react-bootstrap'
import { setAuthedUser } from '../actions/authedUser'

class Login extends Component {
  state = {
    user : ''
  }
  handleSelectProfil = (e) => {
    const user = e.target.value
    this.setState(() => ({
      user
    }))
  }
  handleSubmit = (e) => {
    e.preventDefault()
    const { state } = this.state
    const { dispatch, authedUser } = this.props
    const question = formatQuestion({ optionOneText : optionOne, optionTwoText : optionTwo, author : authedUser})
    dispatch(handleAddQuestion(question))
    this.setState(() => ({
      toHome : true,
    }))
  }
  render () {
    return (
      <div className='center'>
        <h3>Login</h3>
        <h5>{this.state.user}</h5>
        <Form >
          <h5>Please select your profil : </h5>
            <Form.Group>
              <Form.Control as="select" onChange={this.handleSelectProfil} value={this.props.authedUser}>            
                {Object.keys(this.props.users).map((user) => (
                  <option key = {this.props.users[user].id} >
                    {this.props.users[user].id}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Button
              className='btn'
              disabled={user === ''}
              onClick={this.handleSubmit}>
                Submit
            </Button>
        </Form>
      </div>
    )
  }
}

function mapStateToProps ({users}) {

  return {
    users,
  }
}

export default connect(mapStateToProps)(Login)
