import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Form, Button } from 'react-bootstrap'
import { setAuthedUser } from '../actions/autheduser'

class Login extends Component {
  state = {
    user : ''
  }
  handleSelectProfil = (e) => {
    const user = e.target.value
    this.setState(() => ({
      user
    }))
  }
  handleSubmit = (e) => {
    e.preventDefault()
    console.log('ici', this.state.user)
    const user = this.state.user
    const { dispatch } = this.props
    dispatch(setAuthedUser(user))
  }
  render () {
    return (
      <div className='center'>
        <h3>Login</h3>
        <h5>{this.props.authedUser}</h5>
        <Form >
            <Form.Group>
              <Form.Control as="select" onChange={this.handleSelectProfil}> 
              <option value="" >Select your profil</option>           
                {Object.keys(this.props.users).map((user) => (
                  <option key = {this.props.users[user].id} value = {this.props.users[user].id}>
                    {this.props.users[user].id}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Button
              className='btn'
              disabled={this.state.user === ''}
              onClick={this.handleSubmit}>
                Submit
            </Button>
        </Form>
      </div>
    )
  }
}

function mapStateToProps ({users, authedUser}) {



  return {
    users,
    authedUser : authedUser 
    ? authedUser
    : null,
  }
}

export default connect(mapStateToProps)(Login)
