import { RECEIVE_QUESTIONS, ADD_RESPONSE, ADD_QUESTION } from '../actions/questions'

export default function questions (state = {}, action) {
  switch(action.type) {
    case RECEIVE_QUESTIONS : 
      return {
        ...state,
        ...action.questions,
      }
    case ADD_RESPONSE : 
    const { authedUser, qid, answer } = action
     return {
       ...state,
       [qid] : {
         ...state[qid],
         [answer] : {
           ...state[qid][answer],
           votes: state[qid][answer].votes.concat([authedUser])
         }         
       }
     }
    case ADD_QUESTION:
    const { author, id, optionOne, optionTwo } = action
      return {
        ...state,
        [id]: {
          ...state[id],
          [optionOne]: {
            ...state[id][optionOne],
            votes: state[id][optionOne].votes.concat([author])
          },
          [optionTwo]: {
            ...state[id][optionTwo],
            votes: state[id][optionTwo].votes.concat([author])
          }
        }
      }
    default : 
      return state
  }
}
