import { RECEIVE_QUESTIONS, ADD_RESPONSE } from '../actions/questions'

export default function questions (state = {}, action) {
  switch(action.type) {
    case RECEIVE_QUESTIONS : 
      return {
        ...state,
        ...action.questions,
      }
    case ADD_RESPONSE : 
     return {
       ...state,
       [action.id] : {
         ...state[action.id],
         answer : action.answer !== null
          ? action.answer
          : action.answer

       }
     }
    default : 
      return state
  }
}
