import React, { Component } from 'react'
import { connect } from 'react-redux'

class Score extends Component {
  render() {
    const { user } = this.props
    const { id, name, avatarURL } = user
    const numberOfResponse = Object.keys(user.answers).length
    const numberOfQuestionAsk = user.questions.length
    return (
      <div className = 'center'>
        <div className='score-user'>
          <img
            src={require(`../utils/avatars/${avatarURL}`)}
            alt={`Avatar of ${id}`}
            className='avatar'
          />
          <div className='score-user-info'>
            <div>
              <span>{`${name} answer : ${numberOfResponse}`}</span>
              <span>{`${name} ask : ${numberOfQuestionAsk}`}</span>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

function mapStateToProps ({users, questions}, { id }) {
  const user = users[id]

  return {
    user,
    users,
  }
}

export default connect(mapStateToProps)(Score)
