import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Form, Col, Button } from 'react-bootstrap';
import { formatDate } from '../utils/helpers'


class QuestionPage extends Component {
  state = {
    selectedOption: '',
    showResult: false
  }
  optionSelected = (e) => {
    this.setState({
      selectedOption: e.target.id
    })
  }
  handleSubmit = (e) => {
    e.preventDefault()

    this.setState({
      showResult: true
    })
    // const { dispatch, question, authedUser } = this.props
    // const { answer } = this.state.selectedOption


    // dispatch(handleToggleTweet({
    //   id: question.id,
    //   answer,
    //   authedUser
    // }))
  }
  render () {
    const { question, user, authedUser } = this.props
    const { author, timestamp } = question
    const { name, avatarURL } = user

    const ratioAnswer = 100 * question.optionOne.votes.length / (question.optionOne.votes.length + question.optionTwo.votes.length)


    return (
      <div>
        <h2 className='center'> Would You Rather ? </h2>
        <div className='question'>
          <img
            src={require(`../utils/avatars/${avatarURL}`)}
            alt={`Avatar of ${author}`}
            className='avatar-details'
          />
          <div>
            <div className='question-info'>
                <span>{`${name} asks :`}</span>
                <div>{formatDate(timestamp)}</div>
            </div>
            <div className='question-choice'>
            <Form>
                <Form.Group>
                  <Col sm={12}>
                    <Form.Check
                      type="radio"
                      label={question.optionOne.text}
                      name="answer"
                      id="optionOne"
                      onChange={this.optionSelected}
                    />
                    <Form.Check
                      type="radio"
                      label={question.optionTwo.text}
                      name="answer"
                      id="optionTwo"
                      onChange={this.optionSelected}
                    />
                  </Col>
                </Form.Group>
                <p>{this.state.selectedOption}</p>
                <Button disabled = {this.state.selectedOption === ''} onClick={this.handleSubmit}>
                  Submit
                </Button>
                {this.state.showResult === true
                ? <div>
                    <div>{`${question.optionOne.text} : ${ratioAnswer}%`}</div>
                    <div>{`${question.optionTwo.text} : ${1 - ratioAnswer}%`}</div>
                  </div>
                : null
                }
              </Form>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

function mapStateToProps ({authedUser, users, questions}, { id }) {
  const question = questions[id]
  const user = users[question.author]

  return {
    question,
    user,
    authedUser,
  }
}

export default connect(mapStateToProps)(QuestionPage)
