import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import QuestionsContainer from './QuestionsContainer'

class Dashboard extends Component {
  render() {
    if (this.state.authedUser === null) {
      return <Redirect to='/login'/>
    }
    const {remaningQuestions, answeredQuestions } = this.props
    return (
      <div>
        <h2 className='center'> Would You Rather ? </h2>
        <div className='row'> 
          <QuestionsContainer 
            listQuestion = {remaningQuestions}
            title = {'Remaining Question'}
          />
          <QuestionsContainer 
            listQuestion = {answeredQuestions}
            title = {'Answered Question'}
          />
        </div>
      </div>
    )
  }
}

function mapStateToProps ({authedUser, users, questions}) {

  return {
    user : users[authedUser] 
    ? users[authedUser] 
    : null,
    remaningQuestions: authedUser
    ? Object.keys(questions).filter(qid => !Object.keys(users[authedUser].answers).includes(qid))
      .sort((a, b) => questions[b].timestamp - questions[a].timestamp)
    : null,
    answeredQuestions : authedUser
    ? Object.keys(users[authedUser].answers)
      .sort((a, b) => questions[b].timestamp - questions[a].timestamp)
    : null
  }
}

export default connect(mapStateToProps)(Dashboard)
