import React, { Component } from 'react'
import { connect } from 'react-redux'
import Table from 'react-bootstrap/Table'

class LeaderBoard extends Component {
  render() {
    return (
      <Table responsive size="sm">
        <thead>
          <tr>
            <th>Users</th>
            <th>Question Ask</th>
            <th>Question Answer</th>
            <th>Total Score</th>
          </tr>
        </thead>
        <tbody>
        {Object.keys(this.props.users).map((user) => {
          const { id, name, avatarURL, answers, questions } = this.props.users[user]
          const numberOfResponse = Object.keys(answers).length
          const numberOfQuestionAsk = questions.length
          return (
            <tr>
              <td>          
                <img
                  src={require(`../utils/avatars/${avatarURL}`)}
                  alt={`Avatar of ${id}`}
                  className='avatar'
                />
                <p>{name}</p>
              </td>
                <td>{numberOfResponse}</td>
                <td>{numberOfQuestionAsk}</td>
                <td>{numberOfResponse + numberOfQuestionAsk}</td>
            </tr>
          )})}
        </tbody>
      </Table>
    )
  }
}

function mapStateToProps ({users}, { id }) {
  const user = users[id]

  return {
    user,
    users,
  }
}

export default connect(mapStateToProps)(LeaderBoard)
